#!/usr/bin/env node

const express = require('express')
const serve = express.static
const path = require('path')

const paths = { docs: path.join(__dirname, '../dist') }
const port = process.env.DOC_PORT || 8095

const app = express()
app.use('/', serve(paths.docs))
app.listen(port, () => console.log(`Documentation server is listening on http://localhost:${port}/`))
