#!/usr/bin/env node
const fs = require('fs-extra')
const path = require('path')
const chalk = require('chalk')

const folderName = process.argv[2] || 'findy-sources-workspace'
const targetPath = path.join(process.cwd(), folderName)

fs.copySync(
  path.join(__dirname, '..', 'templates', 'workspace'),
  targetPath
)

console.log(`
Source development workspace was created in folder ${targetPath}

Start development with following commands:

${chalk.blue(`cd ${folderName}
npm install
npm run create <your-source-name>
npm run dev`)}`)
