#!/usr/bin/env node

const path = require('path')
const paths = {
  reports: process.env.REPORTS_PATH || 'reports',
  app: path.join(__dirname, '../build')
}

const express = require('express')
const index = require('serve-index')
const serve = express.static

const app = express()

app.use('/reports', serve(paths.reports), index(paths.reports))
app.use('/', serve(paths.app))

app.listen(8090, () => console.log('Statistics viewer is listening on http://localhost:8090/'))