#!/usr/bin/env node
const path = require('path')
if (!process.env.SOURCES_LIB) {
  process.exitCode = 1
  console.error('SOURCES_LIB is not set')
} else {
  const { Sources } = require(path.resolve(process.cwd(), process.env.SOURCES_LIB))
  require('../dist/index.js').default(Sources).catch(e => console.error(e, e.stackTrace))
}
